mapper.delete:
DELETE FROM BOARD_CTG  A
      WHERE A.NO = #{ctgDelNo}
        AND A.CLAN = getClan(#{email})

mapper.update:
UPDATE BOARD_CTG A
   SET A.NM = #{ctgUpdNm}
 WHERE A.NO = #{ctgDelNo}
   AND A.CLAN = getClan(#{email})

UPDATE BOARD_CTG A
   SET A.SORT = #{SORT}
 WHERE A.NO = #{NO}
   AND A.CLAN = getClan(#{email})

mapper.select:
SELECT RES.EMAIL , RES.NICK , RES.AUTH FROM ( SELECT A.EMAIL AS EMAIL , A.NICK AS NICK , CASE WHEN A.AUTH = 3 THEN '마스터' WHEN A.AUTH = 2 THEN '매니저' ELSE '일반' END AS AUTH , ROW_NUMBER() OVER(ORDER BY A.NICK ASC) AS RNK FROM ACCOUNT A WHERE A.CLAN = getClan(#{email}) AND A.AUTH != 0
AND A.NICK LIKE #{usrNick}
) RES WHERE RES.RNK BETWEEN #{startCnt} AND #{endCnt}

SELECT A.AUTH AS AUTH
  FROM ACCOUNT A
 WHERE A.EMAIL = #{email}

mapper.delete:
DELETE FROM ACCOUNT
      WHERE EMAIL = #{usrEmail}
        AND CLAN  = getClan(#{sEmail})

DELETE FROM ACCOUNT
 WHERE EMAIL = #{joinEmail}
   AND CLAN  = getClan(#{email})

