mapper.insert:
INSERT INTO ELO_HIST(
     CLAN
   , CW
   , EMAIL
   , BEF_ELO
   , PM_ELO
   , AFT_ELO
   , NOTE
   , WDATE
  ) 
VALUES

(
     getClan(#{sEmail})
   , #{no}
   , #{item.email_txt}
   , #{item.pre_elo}
   , #{item.pm_elo}
   , #{item.aft_elo}
   , '내전(정기) 결과 반영'
   , NOW()
)

update.foreach:
UPDATE ACCOUNT A
   SET A.ELO = #{item.aft_elo}
 WHERE A.EMAIL = #{item.email_txt}
   AND A.CLAN = getClan(#{sEmail})

UPDATE ACCOUNT_WITHDRAW A
   SET A.ELO = #{item.aft_elo}
 WHERE A.EMAIL = #{item.email_txt}
   AND A.CLAN = getClan(#{sEmail})

mapper.select:
SELECT RES.EMAIL AS EMAIL , RES.NICK AS NICK , RES.ELO AS ELO FROM ( SELECT A.EMAIL AS EMAIL , A.NICK AS NICK , A.ELO AS ELO , ROW_NUMBER() OVER(ORDER BY A.NICK ASC) AS RNK FROM ACCOUNT A WHERE A.CLAN = getClan(#{sEmail})
AND A.NICK LIKE #{usrNick}
) RES WHERE RES.RNK BETWEEN #{startCnt} AND #{endCnt}

mapper.insert:
INSERT INTO ELO_HIST (
                       CLAN
                     , EMAIL
                     , BEF_ELO
                     , PM_ELO
                     , AFT_ELO
                     , NOTE
                     , WDATE
                     )
              VALUES (
                       getClan(#{sEmail})
                     , #{usrEmail}
                     , getElo(#{usrEmail}, #{sEmail})
			    
			         , #{eloSc}
			         , getElo(#{usrEmail}, #{sEmail}) + #{eloSc}
				
			    
			         , #{eloSc} * (-1)
			         , getElo(#{usrEmail}, #{sEmail}) - #{eloSc}
				
                     , #{eloNote}
                     , NOW()
                     )

mapper.update:
UPDATE ACCOUNT A
   
   SET A.ELO = getElo(#{usrEmail},#{sEmail}) + #{eloSc}
   
   
   SET A.ELO = getElo(#{usrEmail},#{sEmail}) - #{eloSc}
   
 WHERE A.EMAIL = #{usrEmail}
   AND A.CLAN = getClan(#{sEmail})
